// Input: items from previous node (Gemini output)
let output = [];

// Loop through each item from Gemini
items.forEach(item => {
    // Get the string with the JSON inside
    let raw = item.json.content.parts[0].text;

    // Remove ```json wrapping
    raw = raw.replace(/```json\s*/, '').replace(/\s*```$/, '');

    // Parse it as JSON array
    let parsed;
    try {
        parsed = JSON.parse(raw);
    } catch (err) {
        throw new Error('Failed to parse Gemini JSON: ' + err);
    }

    // Map each object in the parsed array to n8n-friendly format
    parsed.forEach(p => {
        output.push({
            json: {
                Message: p.message,
                "Slack Channel ID": p.slack_channel_id
            }
        });
    });
});

// Return the properly formatted array
return output;

