// n8n Code node (JavaScript)

// 1. Grab the incoming data (can be object, array, or string)
let raw = $input.first().json.content.parts[0].text;

// 2. Parse if it's a string
if (typeof raw === "string") {
  try {
    raw = JSON.parse(raw);
  } catch (e) {
    throw new Error("Input is not valid JSON: " + raw);
  }
}

// 3. If it's a single object, wrap it in an array
if (!Array.isArray(raw)) {
  raw = [raw];
}

// 4. Clean each task, keeping only the needed fields
const cleaned = raw.map(task => ({
  "Task Name": task["Task Name"] || "",
  "Assigned To": task["Assigned To"] || "",
  "Status": task["Status"] || "Not started",
  "Due Date": task["Due Date"] || " ",
  "Client-Project": task["Client-Project"] || ""
}));

// 5. Return as n8n array of items
return cleaned.map(t => ({ json: t }));

